import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
const Header = () => {
  const navigate = useNavigate();
  const [file, setFile] = useState(null);
  const [filename, setFilename] = useState('');

  const handleImageChange = (event) => {
    const files = event.target.files[0];
    if (files) {
      setFile(files);
      setFilename(files.name);
      console.log("File selected:", files);
    }
  };

  const handleUpload = async (event) => {
    event.preventDefault();

    if (!file) {
      alert("Please select a file first.");
      return;
    }

    const formData = new FormData();
    formData.append("image", file);

    try {
      // window.location.href = `/result`;
      const response = await fetch("http://ec2-3-86-212-60.compute-1.amazonaws.com:5000/predict", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
       
        const result = await response.json();
        console.log("File uploaded successfully:", result);
        navigate("/result",{state:{result}})
      
      }else{
        const result = await response.json();
        console.log("File uploaded successfully:", result);
        navigate("/result",{state:{result}})
      }
    } catch (error) {
      console.error("Error during upload:", error);
      alert("Error during upload.");
    }
  };

  return (
    <div className="flex flex-col justify-center items-center text-center my-20">
      <div className="text-gray-500 inline-flex items-center gap-2 bg-white px-6 py-1 rounded-full border border-neutral-500">
        <p>Scan. Verify. Trust. Ensuring Authentic Medical Certificates.</p>
        <img src="" alt="" className="h-6" />
      </div>

      <h1 className="text-3xl max-w-[300px] sm:text-7xl sm:max-w-[590px] mx-auto mt-10 text-center">
        Spot <span className="text-blue-600">Fake </span> Medical Certificates
        Instantly with AI!
      </h1>

      <p className="text-center max-w-xl mx-auto mt-5">
        Our system verifies QR code URLs on medical certificates to detect
        fraudulent or genuine documents, ensuring secure and tamper-proof
        authentication for hospitals, employers, and authorities.
      </p>

      <form action="" onSubmit={handleUpload} name="upload-form">
      <button
        type="button"
        className="sm:text-lg text-white bg-black w-auto mt-8 px-12 py-2.5 flex items-center gap-2 justify-center rounded-full mx-auto"
        onClick={() => document.getElementById("file-input").click()}
      >
        <input
          id="file-input"
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          className="hidden"
        />
        Upload
        <img src="data:image/png;base64," alt="" className='h-6 rotate-180' />
      </button>
      {filename && <p className=" text-sm transition-transform">{filename} selected</p>}
      <button
        type="submit"
        className="sm:text-lg text-white bg-black w-auto mt-2 px-12 py-2.5 flex items-center gap-2 justify-center rounded-full mx-auto"
      >
Authenticate      </button>
    </form>
    </div>
  );
};

export default Header;
